import { User } from "../user.model";
import { AUTH_AUTHENCATED, AUTH_AUTHENCATE_FAIL, AUTH_CLEAR_ERROR, AUTH_LOGIN_START, AUTH_LOGOUT, AUTH_SIGNUP_START } from "./auth.action";
const initialState = {
  user: null,
  authError: null,
  isLoading: false,
  redirect: false
};
export function authReducer(state = initialState, action) {
  switch (action.type) {
    case AUTH_AUTHENCATED:
      console.log(action.payload);
      const user = new User(action.payload.email, action.payload.id, action.payload.token, action.payload.tokenExpirationDate);
      return Object.assign(Object.assign({}, state), {
        user: user,
        authError: null,
        isLoading: false
      });

    case AUTH_LOGOUT:
      console.log('reducer');
      return Object.assign(Object.assign({}, state), {
        user: null,
        authError: null,
        isLoading: false
      });

    case AUTH_LOGIN_START:
      return Object.assign(Object.assign({}, state), {
        authError: null,
        isLoading: true
      });

    case AUTH_SIGNUP_START:
      return Object.assign(Object.assign({}, state), {
        authError: null,
        isLoading: true
      });

    case AUTH_AUTHENCATE_FAIL:
      return Object.assign(Object.assign({}, state), {
        user: null,
        authError: action.payload,
        isLoading: false
      });

    case AUTH_CLEAR_ERROR:
      return Object.assign(Object.assign({}, state), {
        authError: null
      });

    default:
      return state;
  }
}
