import { RECEIPE_ADD_RECEIPE, RECEIPE_DELETE_RECEIPE, RECEIPE_SET_RECEIPES, RECEIPE_UPDATE_RECEIPE } from "./receipe.actions";
const initialState = {
  receipes: []
};
export function receipeReducer(state = initialState, action) {
  switch (action.type) {
    case RECEIPE_SET_RECEIPES:
      return Object.assign(Object.assign({}, state), {
        receipes: [...action.payload]
      });

    case RECEIPE_ADD_RECEIPE:
      return Object.assign(Object.assign({}, state), {
        receipes: [...state.receipes, action.payload]
      });

    case RECEIPE_UPDATE_RECEIPE:
      const updatedReceipe = Object.assign(Object.assign({}, state.receipes[action.payload.index]), action.payload.receipe);
      const updatedReceipes = [...state.receipes];
      updatedReceipes[action.payload.index] = updatedReceipe;
      return Object.assign(Object.assign({}, state), {
        receipes: updatedReceipes
      });

    case RECEIPE_DELETE_RECEIPE:
      return Object.assign(Object.assign({}, state), {
        receipes: state.receipes.filter((receipe, index) => {
          return index !== action.payload;
        })
      });

    default:
      return state;
  }
}
